import logo from './logo.svg';
import { useState } from 'react';

function ApiFetch() {

    const [fetchCountry, setCountry] = useState([]);
    const [inputValue, setInputValue] = useState('');

    const handleInputChange = (e) => {
        setInputValue(e.target.value);
      };


    function clickSearch() {

        fetch("https://restcountries.com/v3.1/name/${clickSearch}").
            then((response) => response.json())
            .then((country) => { setCountry(country) });
    }



    return (
        <div>



            <input type="text"  placeholder="country name" value={inputValue}  onClick={handleInputChange}/>
            <button type='button' onClick={clickSearch}>Get</button>

            <div className='dataMain'>
                {fetchCountry.map((country, index) => (
                    <div className='dataList' key={index}>

                        <p> <strong>Name:</strong> ({country.common})</p>
                        {/* <p> <strong>Official Name:</strong> ({country.official})</p>
                        <p><strong>Capital:</strong> {country.capital}</p>
                        <p><strong>Region:</strong> {country.region}</p>
                        <p> <strong>Languages:</strong> ({country.languages})</p> */}

                    </div>

                ))}

            </div>



        </div>
    );
}

export default ApiFetch;
