import { useState } from 'react';

function ApiFetch() {
    const [fetchCountry, setCountry] = useState([]);
    const [inputValue, setInputValue] = useState('');
    // const [displayValue, setDisplayValue] = useState('');

    const handleInputChange = (e) => {
        setInputValue(e.target.value);
    };

    const getCountry = () => {

        clickSearch(inputValue); 
    };

    function clickSearch() {

        fetch("https://restcountries.com/v3.1/name/${inputValue}").
            then((response) => response.json())
            .then((country) => { setCountry(country) });
    }

    return (
        <div>
            <input
                type="text"
                placeholder="Country name"
                value={inputValue}
                onChange={handleInputChange}
            />
            <button type="button" onClick={getCountry}>
                Get
            </button>

            {/* <p>{inputValue}</p> */}

            <div className='dataMain'>
      {fetchCountry.map((country, index) => (
      <div className='dataList' key={index}>

         <p><strong>Name:</strong>({country.name.common})</p>
         {/* <p><strong>Capital:</strong>({country.capital})</p>
         <p><strong>Official Name</strong>({country.Name})</p> */}
         <p><strong>Languages</strong>({country.languages.eng})</p>

          </div>

      ))}

</div>
        </div>
    );
}

export default ApiFetch;
