    const [displayValue, setDisplayValue] = useState('');



    const handleInputChange = (event) => {
        setInputValue(event.target.value);
    };

    const getCountry = () => {
        setDisplayValue(inputValue);
    };


    // function clickSearch() {

    //     fetch("https://restcountries.com/v3.1/name/${clickSearch}").
    //         then((response) => response.json())
    //         .then((country) => { setCountry(country) });
    // }


    return (
        <div>


            <input type="text" placeholder="country name" value={inputValue} onClick={handleInputChange} />
            <button type='button' onClick={getCountry}>Get</button>

            <p>{displayValue}</p>






        </div>
    );
}

export default ApiFetch;
