import logo from './logo.svg';
import { useState } from 'react';

function ApiFetch() {

    const [fetchCountry, setCountry] = useState([]);


    const [inputValue, setInputValue] = useState('');
  const [displayValue, setDisplayValue] = useState('');

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleButtonClick = () => {
    setDisplayValue(inputValue);
  };


    function clickSearch() {

        fetch("https://restcountries.com/v3.1/name/${clickSearch}").
            then((response) => response.json())
            .then((country) => { setCountry(country) });
    }






    return (
        <div>

<input
        type="text"
        value={inputValue}
        onChange={handleInputChange}
        placeholder="Enter text"
      />
      <button onClick={handleButtonClick}>Show Value</button>
      <p>Value: {displayValue}</p>

            <input type="text"  placeholder="country name" value={clickSearch} />
            <button type='button' onClick={clickSearch}>Get</button>

            <div className='dataMain'>
                {fetchCountry.map((country, index) => (
                    <div className='dataList' key={index}>

                        <p> <strong>Name:</strong> ({country.common})</p>
                        {/* <p> <strong>Official Name:</strong> ({country.official})</p>
                        <p><strong>Capital:</strong> {country.capital}</p>
                        <p><strong>Region:</strong> {country.region}</p>
                        <p> <strong>Languages:</strong> ({country.languages})</p> */}

                    </div>

                ))}

            </div>



        </div>
    );
}

export default ApiFetch;
