import { useState } from 'react';

function ApiFetch() {
    const [fetchCountry, setCountry] = useState();
    const [inputValue, setInputValue] = useState('');

    const handleInputChange = (e) => {
        setInputValue(e.target.value);
    };

    const getCountry = () => {
        fetch(`https://restcountries.com/v3.1/name/${inputValue}`).
            then((response) => response.json())
            .then((country) => { setCountry(country[0]) });

    };

    return (
        <div>
            <input
                type="text"
                placeholder="Country name"
                value={inputValue}
                onChange={handleInputChange}
            />
            <button type="button" onClick={getCountry}>
                Get
            </button>

            <div className="dataMain">
                {fetchCountry && (
                    <div className="dataList">
                        <p><strong>Name:</strong> {fetchCountry.name}</p>
                        <p><strong>Languages:</strong> {Object.values(fetchCountry.languages || {}).join(', ')}</p>
                        <img src={fetchCountry.flags} alt="Flag" width="100" />
                    </div>
                )}
            </div>
        </div>
    );
}

export default ApiFetch;
