import { useState } from 'react';

function ApiFetch() {
    const [fetchCountry, setCountry] = useState();
    const [inputValue, setInputValue] = useState('');
    const [error, setError] = useState();

    const handleInputChange = (e) => {
        setInputValue(e.target.value);
    };

    const getCountry = () => {
        fetch(`https://restcountries.com/v3.1/name/${inputValue}`).
            then((response) => response.json())
            .then((country) => {
                if (fetchCountry.status==404){

                    setError();

                }else{
                    setCountry(country[0]) 
                }


            });

    };

    return (
        <div>
            <input
                type="text"
                placeholder="Country name"
                value={inputValue}
                onChange={handleInputChange}
            />
           {error ? (<span className="errors">Not Found</span>) : null}



            <button type="button" onClick={getCountry}>
                Get
            </button>

            <div className="dataMain">
                {fetchCountry && (
                    <div className="dataList">
                        <p><strong>Name:</strong> {fetchCountry.name.common}</p>
                        {/* <p><strong>Languages</strong>({fetchCountry.languages})</p> */}
                        <img src={fetchCountry.flags.png} alt="Flag" width="100" />
                    </div>
                )}
            </div>
        </div>
    );
}

export default ApiFetch;
