import { useState } from 'react';

function ApiFetch() {
    const [fetchCountry, setFetchCountry] = useState();
    const [inputValue, setInputValue] = useState();
    const [error, setError] = useState(); 

    const handleInputChange = (e) => {
        setInputValue(e.target.value);
    };

    const getCountry = () => {
        if (!inputValue.trim()) {
            setError("Please enter a country name.");
            return;  // Early return if input is empty
        }

        fetch(`https://restcountries.com/v3.1/name/${inputValue}`)
            .then((response) => {
                if (!response.ok) {
                    setError("Country not found");
                    throw new Error("Country not found"); // Throw error to skip next then
                }
                return response.json();
            })
            .then((country) => {
                setFetchCountry(country[0]);
                setError('');  // Reset error on successful fetch
            })
            .catch(() => {
                setError("Country not found");
            });
    };
    return (
        <div>
            <input
                type="text"
                placeholder="Country name"
                value={inputValue}
                onChange={handleInputChange}
            />
            {error && <span className="errors">Country not found</span>}

            <button type="button" onClick={getCountry}>
                Get
            </button>

            <div className="dataMain">
                {fetchCountry && (
                    <div className="dataList">
                        <p><strong>Name:</strong> {fetchCountry.name.common}</p>
                        <img src={fetchCountry.flags.png} alt="Flag" width="100" />
                    </div>
                )}
            </div>
        </div>
    );
}

export default ApiFetch;
