import { useState } from 'react';

function ApiFetch() {
    const [fetchCountry, setCountry] = useState([]);
    const [inputValue, setInputValue] = useState('');
    // const [displayValue, setDisplayValue] = useState('');

    const handleInputChange = (e) => {
        setInputValue(e.target.value);
        console.log(inputValue)
    };

    const getCountry = () => {

        clickSearch(inputValue);
    };

    function clickSearch() {

        fetch(`https://restcountries.com/v3.1/name/${inputValue}`).
            then((response) => response.json())
            .then((country) => { setCountry(country) });
    }

    return (
        <div>
            <input
                type="text"
                placeholder="Country name"
                value={inputValue}
                onChange={handleInputChange}
            />
            <button type="button" onClick={getCountry}>
                Get
            </button>

            {/* <p>{inputValue}</p> */}


            <div className="dataMain">
                {fetchCountry && (
                    <div className="dataList">
                        <p><strong>Name:</strong>({fetchCountry.name})</p>

                        <p><strong>Languages</strong>({fetchCountry.languages})</p>
                        <img src={fetchCountry.flags} alt="Flag" width="100" />
                    </div>
                )}
            </div>

        </div>
    );
}

export default ApiFetch;
