import { useState } from 'react';

function ApiFetch() {
    const [fetchCountry, setCountry] = useState([]);
    const [inputValue, setInputValue] = useState('');
    const [displayValue, setDisplayValue] = useState('');

    const handleInputChange = (event) => {
        setInputValue(event.target.value);
    };

    const getCountry = () => {
        setDisplayValue(inputValue);
        clickSearch(inputValue); // Call clickSearch function with input value
    };

    function clickSearch(countryName) {
        fetch(`https://restcountries.com/v3.1/name/${countryName}`)
            .then((response) => response.json())
            .then((country) => setCountry(country))
            .catch((error) => console.error("Error fetching country data:", error));
    }

    return (
        <div>
            <input
                type="text"
                placeholder="Country name"
                value={inputValue}
                onChange={handleInputChange}
            />
            <button type="button" onClick={getCountry}>
                Get
            </button>

            {/* <p>{displayValue}</p> */}

            <div className='dataMain'>
                {fetchCountry.map((country, index) => (
                    <div className='dataList' key={index}>
                        <p><strong>Name:</strong> {country.name.common}</p>

                        <p><strong>Official Name:</strong> {country.name.official}</p>
                        <p><strong>Capital:</strong> {country.capital}</p>
                        <p><strong>Region:</strong> {country.region}</p>
                        <p><strong>Languages:</strong> {Object.values(country.languages).join(', ')}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default ApiFetch;
