import logo from './logo.svg';
import { useState } from 'react';

function ApiFetch() {



    const [fetchCountryName, setCountry] = useState([]);


  function handleSearch() {

    fetch("https://restcountries.com/v3.1/name/${fetchCountryName}").
      then((response) => response.json())
      .then((data) => { setCountry(data.data) });
  }



  return (
    <div>

      <input type="text"  placeholder="country name" value={handleSearch} />
      {/* <button type='button' onClick={handleSearch}>Get</button> */}

      <div className='dataMain'>
      {fetchCountryName.map((country, index) => (
      <div className='dataList' key={index}>

           <p> <strong>Name:</strong> ({country.common})</p>

          </div>

      ))}

</div>



    </div>
  );
}

export default ApiFetch;
